/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `dashboard_queries`(IN `query_type` VARCHAR(20),IN `_agent_id` VARCHAR(100),IN `_start_date` DATE,IN `_end_date` DATE,IN `_status` VARCHAR(100),IN `_tenant_id` VARCHAR(100),IN `_plaza_id` VARCHAR(10),IN `_phase_id` VARCHAR(10))
BEGIN 
  DECLARE bus_acct_no VARCHAR(11);
  
  SELECT a.account_no INTO bus_acct_no FROM accounts a WHERE a.sector ='Rental services';
  
  IF query_type = 'dashboard-reports' THEN
   IF _plaza_id != '*' AND _plaza_id > 0 THEN
        SELECT
            (SELECT COUNT(tenant_id) FROM shops WHERE plaza_id=_plaza_id) as total_tenants,
            (SELECT COUNT(tenant_id) FROM shops WHERE plaza_id=_plaza_id AND status='expired') as expired_rents,
            IFNULL(SUM(p.cr), 0) as total_payments,
            IFNULL(SUM(p.dr), 0) as expected_payments,
            (SELECT IFNULL(COUNT(id), 0) FROM shops WHERE plaza_id=_plaza_id AND (tenant_id IS NULL OR status='vacant')) as vacant_shops,
            (SELECT IFNULL(COUNT(id), 0) FROM shops WHERE plaza_id=_plaza_id) as total_shops,
            (SELECT IFNULL(COUNT(id), 0) FROM tenants WHERE status='active') as total_clients,
            (SELECT IFNULL(COUNT(id), 0) FROM plazas) as total_plazas
        FROM rental_transactions p
        JOIN shops s ON s.id = p.shop_id
        WHERE 
            p.receiver_account = bus_acct_no
            AND s.plaza_id = _plaza_id
            AND p.transaction_date BETWEEN _start_date AND _end_date;
    ELSE 
        SELECT
            (SELECT COUNT(tenant_id) FROM shops) as total_tenants,
            (SELECT COUNT(tenant_id) FROM shops WHERE status='expired') as expired_rents,
            IFNULL(SUM(p.cr), 0) as total_payments,
            IFNULL(SUM(p.dr), 0) as expected_payments,
            (SELECT IFNULL(COUNT(id), 0) FROM shops WHERE tenant_id IS NULL OR status='vacant') as vacant_shops,
            (SELECT IFNULL(COUNT(id), 0) FROM shops) as total_shops,
            (SELECT IFNULL(COUNT(id), 0) FROM tenants WHERE status='active') as total_clients,
            (SELECT IFNULL(COUNT(id), 0) FROM plazas) as total_plazas
        FROM rental_transactions p
        JOIN shops s ON s.id = p.shop_id
        WHERE 
            p.receiver_account = bus_acct_no
            AND p.transaction_date BETWEEN _start_date AND _end_date;
    END IF;
  ELSEIF query_type = 'rental-reports' OR query_type = 'select' THEN
    IF _status = 'all-payments' THEN
      SELECT p.*, p.account_no as tenant_id, p.account_name as tenant_name, p.dr as amount_paid, s.shop_no as shop_no, s.name as phase_name, s.rent_fee, s.phase_id FROM rental_transactions p JOIN shops s ON (s.id = p.shop_id) WHERE p.dr > 0 AND p.account_no != bus_acct_no AND p.status !='approved';
    ELSEIF _status ='expired-rents' THEN
      IF _phase_id > 0 THEN
        SELECT COUNT(created_at) as total_expired_rents  FROM `shop_allocations` s WHERE s.rent_end_date <= _end_date AND s.phase_id = _phase_id;
      ELSE
        SELECT COUNT(created_at) as total_expired_rents  FROM `shop_allocations` s WHERE s.rent_end_date <= _end_date;
      END IF;
    ELSEIF _status ='tenants' THEN
      IF _phase_id > 0 THEN
        SELECT COUNT(id) as total_tenants FROM tenants WHERE phase_id = _phase_id;
      ELSEIF _plaza_id > 0 THEN
        SELECT COUNT(id) as total_tenants FROM tenants WHERE plaza_id = _plaza_id;
      ELSE
        SELECT COUNT(id) as total_tenants FROM tenants;
      END IF;
    ELSEIF _status ='vacant-shops' THEN
      IF _phase_id > 0 THEN
        SELECT COUNT(id) as total_vacant_shops WHERE phase_id = _phase_id;
      ELSEIF _plaza_id > 0 THEN
        SELECT COUNT(id) as total_vacant_shops WHERE plaza_id = _plaza_id;
      ELSE
        SELECT COUNT(id) as total_vacant_shops;
      END IF;
    ELSEIF _status ='tenants-debts' THEN
      SELECT COUNT(id) as total_tenants FROM `tenants` t WHERE t.balance > 0;
    ELSEIF _status ='tenants-status' THEN
      SELECT COUNT(id) as total_tenants FROM `tenants` WHERE status = _status;
    ELSEIF _status ='tenants' THEN
      SELECT COUNT(id) as total_tenants FROM `tenants`;
    END IF;
  END IF;
END*/;;
