/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `power_queries`(IN `query_type` VARCHAR(20),IN `_id` INT(10),IN `_shop_id` INT(10),IN `_agent_id` INT(10),IN `_tenant_id` INT(10),IN `_meter_no` VARCHAR(20),IN `_qty` VARCHAR(100),IN `_cost` VARCHAR(100),IN `_description` VARCHAR(100),IN `_year` VARCHAR(4),IN `_month` VARCHAR(2),IN `_status` VARCHAR(100),IN `_start_date` DATE,IN `_end_date` DATE,IN `_from` INT(10),IN `_to` INT(10))
    READS SQL DATA
    DETERMINISTIC
BEGIN 
DECLARE cus_units, cur_balance, _unit_cost DOUBLE;

SELECT total_units INTO cus_units FROM shops WHERE id = _shop_id;

SELECT unit_cost INTO _unit_cost FROM power_settings WHERE status='active';

SELECT dr INTO cur_balance FROM power_transactions pr 
WHERE pr.month=_month AND pr.year=_year AND pr.shop_id=_shop_id; 
 
IF query_type='get-settings' THEN
    IF _start_date IS NOT NULL AND _end_date IS NOT NULL THEN
        SELECT 
    ps.valid_from, 
    ps.valid_till,
    ps.unit_cost,  
    SUM(pt.unit_consumed) AS total_unit_consumed,  
    IFNULL(SUM(pt.cr), 0) AS total_revenue,
    SUM(pt.unit_consumed * ps.unit_cost) AS projected_revenue,
    (IFNULL(SUM(pt.dr), 0) - IFNULL(SUM(pt.cr), 0)) AS pending_revenue, 
    (SELECT COUNT(tenant_id) FROM shops WHERE tenant_id > 0) AS meters,
    (SELECT COUNT(id) FROM tenants WHERE status ='active') AS tenants
FROM power_settings ps
LEFT JOIN power_transactions pt ON DATE(pt.transaction_date) BETWEEN _start_date AND _end_date
WHERE ps.status='active'
GROUP BY ps.valid_from, ps.valid_till, ps.unit_cost;

    ELSE
        SELECT 
            ps.valid_from, 
            IFNULL(ps.valid_till, DATE_ADD(NOW(), INTERVAL 1 MONTH)) AS valid_till,
            ps.unit_cost,  
            SUM(pt.unit_consumed) AS total_unit_consumed,  
            IFNULL(SUM(pt.cr), 0) AS total_revenue,
            SUM(pt.unit_consumed * ps.unit_cost) AS projected_revenue,
            (IFNULL(SUM(pt.dr), 0) - IFNULL(SUM(pt.cr), 0)) AS pending_revenue, 
            (SELECT COUNT(tenant_id) FROM shops WHERE tenant_id > 0) AS meters,
            (SELECT COUNT(id) FROM tenants WHERE status ='active') AS tenants
        FROM power_settings ps   
        LEFT JOIN power_transactions pt ON DATE(pt.transaction_date) BETWEEN DATE(ps.valid_from) AND COALESCE(ps.valid_till, NOW())
        WHERE ps.status='active'
        GROUP BY ps.valid_from, IFNULL(ps.valid_till, DATE_ADD(NOW(), INTERVAL 1 MONTH)), ps.unit_cost;
    END IF;
ELSEIF query_type='payment-grid' THEN 
    SELECT 
    year, 
    month,
    CASE 
        WHEN transaction_type = 'INVOICE' AND (cr > 0 OR dr > 0) THEN 'green' 
        WHEN transaction_type = 'INVOICE' THEN 'red'
        WHEN transaction_type = 'PAYMENT' THEN 'green'
        ELSE 'yellow' 
    END AS color,
    SUM(cr) AS total_paid
FROM power_transactions
WHERE transaction_date BETWEEN '2023-01-01' AND '2023-05-28'
GROUP BY year, month,color;
ELSEIF query_type='vacant-meters' THEN 
    SELECT s.* FROM shops s
    LEFT JOIN power_transactions pt ON s.id = pt.shop_id 
    AND pt.status IN ('approved', 'success', 'pending') 
    AND pt.cr > 0 AND pt.transaction_date BETWEEN _start_date AND _end_date 
    WHERE pt.transaction_id IS NULL AND s.tenant_id IS NULL;
      
ELSEIF query_type='pending-payments' THEN 
    SELECT s.* FROM shops s
    LEFT JOIN power_transactions pt ON s.id = pt.shop_id 
    AND pt.status IN ('approved', 'success', 'pending') 
    AND pt.cr > 0 AND pt.transaction_date BETWEEN _start_date AND _end_date 
    WHERE pt.transaction_id IS NULL AND s.tenant_id > 0;
ELSEIF query_type='new-payments' THEN 
  IF _start_date IS NOT NULL AND _end_date IS NOT NULL THEN
    SELECT pt.*, s.shop_no, s.total_units, s.current_units FROM power_transactions pt
    JOIN shops s ON pt.shop_id = s.id
    WHERE pt.status IN ('pending','paid') AND pt.cr > 0 AND pt.transaction_date BETWEEN _start_date AND _end_date;
  ELSE 
    SELECT pt.*, s.shop_no, s.total_units, s.current_units FROM power_transactions pt
    JOIN shops s ON pt.shop_id = s.id
    WHERE pt.status IN ('pending','paid') AND pt.cr > 0;
  END IF;
  ELSEIF query_type='price-list' THEN 
    SELECT * FROM power_settings;
  ELSEIF query_type='update-price' THEN 
    UPDATE `power_settings` SET `status`='changed' WHERE status='active';
    INSERT INTO `power_settings` (`unit_cost`,`valid_from`,`valid_till`,`status`)
    VALUES(_cost,_start_date, _end_date,'active');
    SELECT * FROM `power_settings`;
  ELSEIF query_type='new-invoice' THEN
         INSERT INTO `power_transactions` (record_id,tenant_id,shop_id,agent_id,meter_no, unit_consumed, dr, status, year, month, unit_cost, transaction_no)
        VALUES (_id,_tenant_id,_shop_id,_agent_id,_meter_no,(_qty-_from),_cost, _status, _year, _month, _unit_cost, FLOOR(RAND() * POW(10, 10)));
        UPDATE shops SET total_units=(IFNULL(_from,0)+_qty),  current_units=(_qty-IFNULL(_from,0))  WHERE id = _shop_id;
   ELSEIF query_type='invoice' THEN
        INSERT INTO `power_transactions` (record_id,tenant_id,shop_id,agent_id,meter_no, unit_consumed, dr, status, year, month, unit_cost, transaction_no)
        VALUES (_id,_tenant_id,_shop_id,_agent_id,_meter_no,_qty,_cost, _status, _year, _month, _unit_cost, FLOOR(RAND() * POW(10, 10)));
        UPDATE shops SET total_units=(_qty),  current_units=(_qty-cus_units)  WHERE id = _shop_id;
  ELSEIF query_type='update-invoice'||query_type='power-payment' THEN
      INSERT INTO `power_transactions` (tenant_id,shop_id,agent_id,meter_no, unit_consumed, cr, status, year, month, unit_cost)
    VALUES (_tenant_id,_shop_id,_agent_id,_meter_no,_qty,_cost, _status, _year, _month, _unit_cost);
   
    IF cur_balance >= _cost THEN
        UPDATE power_transactions SET status='paid' WHERE month=_month AND year=_year AND shop_id=_shop_id; 
    ELSEIF _cost < cus_balance THEN 
        UPDATE power_transactions SET status='part-payment', cr=_cost WHERE month=_month AND year=_year AND shop_id=_shop_id; 
    END IF;
    
   UPDATE shops SET total_units=(cus_units+_qty),  current_units=_qty  WHERE id = _shop_id;
ELSEIF query_type='user-transactions' THEN
  SELECT
      t.year,
      t.month,
      SUM(t.cr) AS cr,
      SUM(t.dr) AS dr,
      IFNULL(SUM(t.dr),0) - IFNULL(SUM(t.cr),0) AS balance,
      t.meter_no,
      t.shop_id,
      t.unit_consumed,
      t.unit_cost,
      t.tenant_id
    FROM
      power_transactions t
      WHERE t.tenant_id =_tenant_id 
      AND t.transaction_date BETWEEN _start_date  AND _end_date
    GROUP BY
    t.year,
    t.month,
    t.meter_no
    ORDER BY
      t.year ASC,
      t.month ASC;
ELSEIF query_type='meter-transactions' THEN
  SELECT * FROM power_transactions t
    WHERE t.tenant_id = _tenant_id 
    AND t.shop_id = _shop_id
    AND t.transaction_date BETWEEN _start_date  AND _end_date
    AND (t.transaction_type = 'INVOICE' OR t.transaction_type = 'PAYMENT')  
    
    ORDER BY t.transaction_date ASC;
  END IF;
END*/;;
DELIMITER ;
