/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `power_transactions`(
    IN query_type VARCHAR(20),
    IN _id INT,
    IN agent_id INT,
    IN tenant_id INT,
    IN shop_id INT,
    IN transaction_date DATE,
    IN transaction_type ENUM('INVOICE', 'PAYMENT', 'DISCOUNT'),
    IN year VARCHAR(4),
    IN month VARCHAR(2),
    IN transaction_method VARCHAR(20),
    IN description VARCHAR(200),
    IN meter_no VARCHAR(20),
    IN unit_consumed DECIMAL(10, 2),
    IN amount DECIMAL(10, 2),
    IN status VARCHAR(10),
    IN transaction_no VARCHAR(100),
    IN receipt VARCHAR(500)
)
BEGIN
    DECLARE last_units,new_consumed, calc_consumed INT;
    DECLARE invoice_dr, payment_cr, balance, unit_cost DECIMAL(10, 2);
    DECLARE org_acct_no, org_acct_name, in_meter_no VARCHAR(50);

    SELECT  
        IFNULL(
            (
                SELECT ps.unit_cost 
                FROM power_settings ps 
                WHERE ps.status = 'active' 
            ), 
            (
                SELECT ps2.unit_cost 
                FROM power_settings ps2 
                WHERE ps2.created_at = (
                    SELECT MAX(created_at) FROM power_settings WHERE status != 'deleted'
                ) 
            )
        ) INTO unit_cost;

    SELECT `account_no`, `description` INTO org_acct_no, org_acct_name FROM accounts WHERE sector = 'Power supply';

    SELECT s.total_units, meter_no INTO last_units, in_meter_no FROM shops s WHERE s.id = shop_id;

    IF query_type = 'create' OR query_type = 'debit' THEN
        IF transaction_type = 'INVOICE' OR query_type = 'debit' THEN
            INSERT INTO power_transactions (
                agent_id,
                tenant_id,
                shop_id,
                transaction_date,
                transaction_type,
                year,
                month,
                transaction_method,
                description,
                meter_no,
                unit_cost,
                unit_consumed,
                cr,
                dr,
                payer_account,
                receiver_account,
                balance,
                status,
                transaction_no
            )
            VALUES (
                agent_id,
                tenant_id,
                shop_id,
                transaction_date,
                transaction_type,
                year,
                month,
                transaction_method,
                description,
                in_meter_no,
                unit_cost,
                unit_consumed,
                '0.0',
                amount,
                tenant_id,
                org_acct_no,
                amount,
                status,
                COALESCE(transaction_no,FLOOR(RAND() * POW(10, 10)))
            );

            UPDATE shops SET total_units = unit_consumed, current_units = (unit_consumed-last_units) WHERE id = shop_id;
        ELSE
            SELECT IFNULL(SUM(pt.dr) - SUM(pt.cr), 0) INTO payment_cr
            FROM power_transactions pt
            WHERE pt.payer_account = payer_account AND pt.shop_id = shop_id;

            INSERT INTO power_transactions (
                agent_id,
                tenant_id,
                shop_id,
                transaction_date,
                transaction_type,
                year,
                month,
                transaction_method,
                description,
                meter_no,
                unit_cost,
                unit_consumed,
                cr,
                dr,
                payer_account,
                receiver_account,
                balance,
                status,
                transaction_no,
                receipt
            )
            VALUES (
                agent_id,
                tenant_id,
                shop_id,
                transaction_date,
                transaction_type,
                year,
                month,
                transaction_method,
                description,
                in_meter_no,
                unit_cost,
                unit_consumed,
                amount,
                '0.0',
                tenant_id,
                org_acct_no,
                IFNULL(payment_cr - amount, 0),
                status,
                COALESCE(transaction_no,FLOOR(RAND() * POW(10, 10))),
                receipt
            );
        END IF;
ELSEIF query_type = 'approve-reciept' AND transaction_no >0   THEN
    UPDATE power_transactions SET status = 'approved' WHERE meter_no = meter_no AND transaction_no = transaction_no AND shop_id=shop_id;

ELSEIF query_type = 'new-invoice' AND status REGEXP '^[0-9]+$' THEN
        SET last_units = CAST(status AS DOUBLE);
        SET new_consumed = (unit_consumed-last_units);
        SET calc_consumed = new_consumed * unit_cost;
    INSERT INTO `power_transactions` (
        agent_id,
        tenant_id,
        shop_id,
        transaction_date,
        transaction_type,
        year,
        month,
        transaction_method,
        description,
        meter_no,
        unit_cost,
        unit_consumed,
        cr,
        dr,
        payer_account,
        receiver_account,
        balance,
        status,
        transaction_no
    )
    VALUES (
        agent_id,
        tenant_id,
        shop_id,
        transaction_date,
        transaction_type,
        year,
        month,
        transaction_method,
        description,
        in_meter_no,
        unit_cost,
        new_consumed,
        '0.0',
        (calc_consumed),
        tenant_id,
        org_acct_no,
        (calc_consumed),
        'pending',
        COALESCE(transaction_no,FLOOR(RAND() * POW(10, 10)))
    ); 
           
            UPDATE shops SET total_units = unit_consumed, current_units = new_consumed WHERE id = shop_id;
       
    
    ELSEIF query_type='view-balance' THEN
        SELECT IFNULL(SUM(t2.dr) - SUM(t2.cr), 0) AS balance
        FROM power_transactions t2
        WHERE t2.payer_account = payer_account AND t2.shop_id = _shop_id;
    ELSEIF query_type = 'delete' THEN
        DELETE FROM power_transactions WHERE id = transaction_id;
    ELSEIF query_type = 'reverse' THEN
        SELECT cr, dr INTO invoice_dr, payment_cr
        FROM power_transactions
        WHERE transaction_id = transaction_id;

        UPDATE power_transactions
        SET cr = payment_cr,
            dr = invoice_dr,
            status = 'REVERSED',
            transaction_type = 'REVERSAL',
            balance = 0
        WHERE id = transaction_id;
    END IF;
END*/;;
DELIMITER ;
