/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `rental_transactions`(
  IN query_type VARCHAR(20),
  IN _id INT,
  IN record_id INT,
  IN user_id INT,
  IN transaction_date DATE,
  IN transaction_type ENUM('INVOICE', 'PAYMENT'),
  IN transaction_method VARCHAR(100),
  IN payer_account INT,
  IN receiver_account INT,
  IN amount DECIMAL(10, 2),
  IN status VARCHAR(10),
  IN transaction_no VARCHAR(100),
  IN _receipt VARCHAR(500)
)
BEGIN
  DECLARE _shop_id INT;
    DECLARE invoice_dr, payment_cr, balance,record_balance,last_balance DECIMAL(10, 2) DEFAULT 0;
    DECLARE bus_acct_no VARCHAR(11);

    SELECT a.account_no INTO bus_acct_no FROM accounts a WHERE a.sector ='Rental services';

  IF record_id > 0 THEN 
    SELECT r.shop_id INTO _shop_id FROM rental_records r WHERE r.id=record_id;
  END IF;
  
  IF query_type = 'create' THEN
    IF transaction_type = 'INVOICE' THEN
      INSERT INTO rental_transactions (
        record_id,
        agent_id,
        shop_id,
        transaction_date,
        transaction_type,
        transaction_mode,
        cr,
        dr,
        payer_account,
        receiver_account,
        status,
        balance,
        transaction_no
      )
      VALUES (
        record_id,
        user_id,
        _shop_id,
        transaction_date,
        transaction_type,
        transaction_mode,
        0.0,
        amount,
        payer_account,
        bus_acct_no,
        status,
        0,
       coalesce(transaction_no, FLOOR(RAND() * POW(10, 10)))
      );

      UPDATE rental_records SET balance = amount WHERE id = record_id;
    ELSE
        SELECT IFNULL(SUM(t2.dr) - SUM(t2.cr), 0) INTO payment_cr
        FROM rental_transactions t2
        WHERE t2.payer_account = payer_account AND t2.shop_id = _shop_id;

        SELECT (r.balance - amount) INTO record_balance FROM rental_records r WHERE r.id = record_id;

        INSERT INTO rental_transactions (
            record_id,
            agent_id,
            shop_id,
            transaction_date,
            transaction_type,
            transaction_mode,
            cr,
            dr,
            payer_account,
            receiver_account,
            status,
            balance,
            transaction_no,
            receipt
        )
        VALUES (
            record_id,
            user_id,
            _shop_id,
            transaction_date,
            transaction_type,
            transaction_mode,
            amount,
            0.0,
            payer_account,
            bus_acct_no,
            status,
            (payment_cr) - amount,
            coalesce(transaction_no, FLOOR(RAND() * POW(10, 10))),
            _receipt
        );
        UPDATE rental_records SET balance = record_balance WHERE id = record_id;
      END IF;

  ELSEIF query_type = 'reject-payment' THEN
    IF (SELECT rx.status FROM rental_transactions rx WHERE rx.transaction_id=_id) != 'rejected' THEN

        
        SELECT IFNULL(r.balance,0)+ amount INTO last_balance
        FROM rental_records r
        WHERE r.id = record_id;
        
        
        UPDATE rental_records
        SET balance = last_balance
        WHERE id = record_id;

      
        UPDATE rental_transactions
        SET status = 'rejected',
        balance = last_balance,
        dr=amount
        WHERE transaction_id = _id;
    END IF;

  ELSEIF query_type = 'update-payment' THEN
   UPDATE rental_transactions r
        SET 
            r.status = status
        WHERE 
        r.payer_account=payer_account AND
        r.shop_id=shop_id AND
        r.transaction_id = _id;
  ELSEIF query_type = 'update' THEN
 
        UPDATE rental_transactions
        SET record_id = record_id,
            agent_id=user_id,
            transaction_date = transaction_date,
            transaction_type = transaction_type,
            cr=amount,
            payer_account = payer_account,
            receiver_account = receiver_account,
            transaction_no = transaction_no,
            status = status
        WHERE transaction_id = _id;

    SET balance = (
      SELECT (SUM(dr) - SUM(cr)) AS balance
      FROM rental_transactions
      WHERE payer_account = payer_account
    );

    UPDATE rental_transactions
    SET balance = balance + amount
    WHERE transaction_id = transaction_id;

  ELSEIF query_type = 'view-transactions' THEN
    SELECT r.*, r.cr as amount_paid
    FROM rental_transactions r
    WHERE r.cr>0; 
  ELSEIF query_type = 'view-transaction' THEN
    SELECT transaction_id, record_id, shop_id, agent_id, transaction_date, transaction_type, debit_credit, payer_account, receiver_account, balance, status, transaction_no
    FROM rental_transactions
    WHERE record_id = record_id;

  ELSEIF query_type = 'list-shops'  || query_type =  'select-meter' THEN
    IF record_id>0 THEN
        SELECT * FROM shops_view s WHERE s.tenant_id = record_id;
    ELSE 
      
      SELECT * FROM shops_view;          
    END IF;
  ELSEIF  query_type =  'select-vacant'  THEN 
    SELECT * FROM shops_view s WHERE s.tenant_id IS NULL;
       
  ELSEIF query_type = 'list-tenants' || query_type =  'select-all'  THEN
    IF record_id>0 THEN
        SELECT * FROM tenants_view t WHERE t.tenant_id = record_id;
    ELSE 
       SELECT * FROM tenants_view;
    END IF;
  END IF;
END*/;;
DELIMITER ;
