/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `restaurant_expenses`(
    IN query_type VARCHAR(20),
    IN expense_id INT,
    IN description TEXT,
    IN amount DECIMAL(10, 2),
    IN expense_date DATE,
    IN date_from DATE,
    IN date_to DATE,
    IN _from INT(10),
    IN _to INT(10),
    IN offset INT(10)
)
BEGIN
    DECLARE sql_query TEXT;
    
    IF query_type = 'create' THEN
        INSERT INTO restaurant_expenses (description, amount, expense_date) 
