 DECLARE CONTINUE HANDLER FOR NOT FOUND SET finished = 1;
 OPEN curTenant;
    getTenant: LOOP
       FETCH curTenant INTO _tenant_balance;
      IF finished = 1 THEN 
        LEAVE getTenant;
      ELSE
        SELECT balance INTO _tenant_balance FROM tenants WHERE id = in_tenant_id;
      END IF; 
  
