    IF total_tenant_money >0 THEN
      IF  (total_tenant_money - _rent_amount) <= 0 THEN
        INSERT INTO `payment_report`(`mode_of_payment`,`account_no`,`agent_id`,`tenant_id`, `account_name`, `cr`, `dr`, `shop_id`, `year`, `status`, `description`,`transacton_id`)
        VALUES(in_payment_method,org_acct_no,in_agent_id,in_tenant_id,org_acct_name,total_tenant_money,0,in_shop_id,in_year,in_status,CONCAT('NGN ',total_tenant_money,' Credited rent payment on ',now()),in_trx_no);
        UPDATE shops SET status = 'paid', `balance` = (shop_balance2 - total_tenant_money) WHERE id = in_shop_id;
        COMMIT;
      ELSE 
        INSERT INTO `payment_report`(`mode_of_payment`,`account_no`,`agent_id`,`tenant_id`, `account_name`, `cr`, `dr`, `shop_id`, `year`, `status`, `description`,`transacton_id`)
        VALUES(in_payment_method,org_acct_no,in_agent_id,in_tenant_id,org_acct_name,total_tenant_money,0,in_shop_id,in_year,in_status,CONCAT('NGN ',total_tenant_money,' Credited rent payment on ',now()),in_trx_no);
        UPDATE shops SET status = 'part-paid', `balance` = (shop_balance2 - total_tenant_money) WHERE id = in_shop_id;
        COMMIT;
      END IF;    
    END IF;     
    END LOOP getTenant;
 CLOSE curTenant;
END*/;;
