/*!50003 CREATE*/ /*!50020 DEFINER=`root`@`localhost`*/ /*!50003 PROCEDURE `shop_queries`(IN `query_type` VARCHAR(20),IN `_id` VARCHAR(100),IN `_name` VARCHAR(100),IN `_rent_fee` VARCHAR(100),IN `_code` VARCHAR(100),IN `_start_date` DATE,IN `_end_date` DATE,IN `_status` VARCHAR(100),IN `_tenant_id` VARCHAR(100),IN `_phase_id` INT(10))
BEGIN 
DECLARE bus_acct_no VARCHAR(11);

SELECT a.account_no INTO bus_acct_no FROM accounts a WHERE a.sector ='Rental services';

  IF query_type='tenant-shops' THEN
   SELECT 
          rental_records.id,
          rental_records.tenant_id,
          rental_records.tenant_name,
          rental_records.shop_no,
          rental_records.shop_id,
          rental_records.rent_fee,
          rental_records.rent_end_date,
          SUM(CASE WHEN rental_transactions.cr = rental_transactions.dr THEN 0 ELSE rental_transactions.cr END) AS amount_paid,
          SUM(CASE WHEN rental_transactions.cr = rental_transactions.dr THEN 0 ELSE rental_transactions.dr END) AS bill_amount,
          SUM( rental_transactions.dr -rental_transactions.cr) AS balance
        FROM 
          rental_transactions
          INNER JOIN rental_records ON rental_transactions.record_id = rental_records.id
        WHERE 
          rental_records.tenant_id =_tenant_id
        GROUP BY 
          rental_records.id,
          rental_records.tenant_id,
          rental_records.tenant_name,
          rental_records.shop_no,
          rental_records.shop_id,
          rental_records.rent_fee,
          rental_records.rent_end_date;
  ELSEIF query_type='select' THEN
    IF _phase_id > 0 THEN
      SELECT * FROM tenants_view t WHERE t.phase_id = _phase_id;
    ELSEIF _id > 0 THEN
      SELECT * FROM tenants_view t WHERE  t.id = _id;
    END IF; 
  ELSEIF query_type='create' THEN 
    INSERT INTO `shops`(`name`, `rent_fee`, `shop_no`, `rent_start_date`, `rent_end_date`, `status`, `tenant_id`, `phase_id`)
    VALUES (name,_rent_fee,_code,start_date,_end_date,_status,_tenant_id,_phase_id);
  ELSEIF query_type='new-payments' THEN
    IF _start_date IS NOT NULL AND _end_date IS NOT NULL THEN
      SELECT r.id as record_id,p.transaction_id, r.rent_fee, r.balance, p.cr as amount_paid, r.tenant_name, r.tenant_id,  r.shop_no, r.shop_id, p.transaction_no, p.receiver_account, p.transaction_date, r.rent_start_date, r.rent_end_date  FROM `rental_transactions` p JOIN rental_records r ON (p.record_id = r.id) WHERE p.cr > 0  AND p.status NOT IN('approved','rejected') AND DATE(p.transaction_date) BETWEEN _start_date AND _end_date ;
    ELSE 
    SELECT r.id as record_id,p.transaction_id, r.rent_fee, r.balance, p.cr as amount_paid, r.tenant_name, r.tenant_id,  r.shop_no, r.shop_id, p.transaction_no, p.receiver_account, p.transaction_date, r.rent_start_date, r.rent_end_date  FROM `rental_transactions` p JOIN rental_records r ON (p.record_id = r.id) WHERE p.cr > 0  AND p.status !='approved';
    END IF;
     ELSEIF query_type='select-vacant' THEN
    IF _phase_id > 0 THEN
        SELECT s.*,(select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s WHERE s.phase_id = _phase_id  AND s.tenant_id IS NULL OR s.status=_status; 
    ELSE
        SELECT s.*,(select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s WHERE  s.tenant_id IS NULL OR s.status=_status; 
    END IF;
   ELSEIF query_type='select-all' THEN
    IF _status = 'transactions' THEN
     IF _id > 0 THEN
        SELECT 
        
          rental_records.id,
          rental_records.tenant_id,
          rental_records.tenant_name,
          rental_records.shop_no,
          rental_records.shop_id,
          rental_records.rent_fee,
          rental_records.rent_end_date,
           SUM(CASE WHEN rental_transactions.cr = rental_transactions.dr THEN 0 ELSE rental_transactions.cr END) AS amount_paid,
          SUM(CASE WHEN rental_transactions.cr = rental_transactions.dr THEN 0 ELSE rental_transactions.dr END) AS bill_amount,
          SUM(CASE WHEN rental_transactions.cr = rental_transactions.dr THEN 0 ELSE rental_transactions.cr END) - SUM(CASE WHEN rental_transactions.cr = rental_transactions.dr THEN 0 ELSE rental_transactions.dr END) AS balance
        FROM 
          rental_transactions 
          INNER JOIN rental_records ON rental_transactions.record_id = rental_records.id
        WHERE 
          rental_records.shop_id =_id AND rental_transactions.transaction_date BETWEEN _start_date AND _end_date
        GROUP BY 
          rental_records.id,
          rental_records.tenant_id,
          rental_records.tenant_name,
          rental_records.shop_no,
          rental_records.shop_id,
          rental_records.rent_fee,
          rental_records.rent_end_date
        HAVING 
          amount_paid != bill_amount; 
    ELSE 
        SELECT p.*, p.payer_account as tenant_id,  p.cr as amount_paid, s.shop_no as shop_no, s.name as phase_name, s.rent_fee, s.phase_id FROM `rental_transactions` p JOIN shops s ON (s.id = p.shop_id) WHERE p.cr > 0 AND p.receiver_account = bus_acct_no  ORDER BY p.transaction_date DESC;
     END IF;
     
    ELSEIF _status ='paid' THEN
      
      SELECT s.*, s.shop_no as shop_no, (SELECT t.name FROM tenants t WHERE t.id=s.tenant_id) AS tenant_name, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s JOIN
      rental_records r ON s.id=r.shop_id
      WHERE r.balance =0 AND s.tenant_id = r.tenant_id;
      
      ELSEIF _status ='part-paid' THEN
      
      SELECT s.*, s.shop_no as shop_no, (SELECT t.name FROM tenants t WHERE t.id=s.tenant_id) AS tenant_name, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s JOIN
      rental_records r ON s.id=r.shop_id
      WHERE r.balance < r.rent_fee  AND s.tenant_id = r.tenant_id;
      
      ELSEIF _status ='paid' THEN
      
      SELECT s.*, s.shop_no as shop_no, (SELECT t.name FROM tenants t WHERE t.id=s.tenant_id) AS tenant_name, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s JOIN
      rental_records r ON s.id=r.shop_id
      WHERE r.balance =0  AND s.tenant_id = r.tenant_id;
      ELSEIF _status ='unpaid' THEN
      
      SELECT s.*, s.shop_no as shop_no, (SELECT t.name FROM tenants t WHERE t.id=s.tenant_id) AS tenant_name, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s JOIN
      rental_records r ON s.id=r.shop_id
      WHERE r.balance =r.rent_fee  AND s.tenant_id = r.tenant_id;
   
      ELSEIF _status ='expired' THEN
      
      SELECT s.*, s.shop_no as shop_no, (SELECT t.name FROM tenants t WHERE t.id=s.tenant_id) AS tenant_name, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s JOIN
      rental_records r ON s.id=r.shop_id
      WHERE DATE(r.rent_end_date) BETWEEN _start_date AND _end_date  AND s.tenant_id = r.tenant_id;
    ELSEIF _status IN ('paid','unpaid','part-paid') THEN 
      SELECT s.*, s.shop_no as shop_no, (SELECT t.name FROM tenants t WHERE t.id=s.tenant_id) AS tenant_name, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s JOIN
      rental_records r ON s.id=r.shop_id
      WHERE s.balance >0 AND s.tenant_id = r.tenant_id;
    ELSEIF _status ='expired' THEN
      SELECT s.*,(select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `rental_records` s WHERE DATE(s.rent_end_date) BETWEEN _start_date AND _end_date;
    ELSEIF _phase_id>0 THEN
      IF _status ='vacant' THEN
        SELECT s.*, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s WHERE s.status =_status AND s.tenant_id IS NULL AND s.phase_id=_phase_id;
      ELSEIF  LENGTH(_status) > 0  AND _status != 'payments' THEN
        SELECT s.*,(select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s WHERE s.phase_id = _phase_id AND status=_status;
      END IF;
    ELSEIF _tenant_id>0 THEN
      SELECT s.*,(select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s WHERE s.tenant_id = _tenant_id;
    ELSEIF _start_date IS NOT NULL AND  _end_date IS NOT NULL THEN
      SELECT s.*, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `rental_records` s WHERE DATE(s.rent_end_date) BETWEEN _start_date AND _end_date;
    ELSE
      SELECT s.*, s.shop_no as shop_no, (select p.name FROM plaza_phases p WHERE s.phase_id=p.id) AS phase_name FROM `shops` s WHERE s.status =_status AND s.tenant_id IS NOT NULL;
    END IF;
 
  ELSEIF query_type='unpaid-meters' THEN
        SELECT pm.id AS id, 
           pm.name AS name, 
           pm.id AS shop_id, 
           pm.shop_no AS shop_no, 
           pm.status AS status, 
           pm.tenant_id AS tenant_id, 
           pm.plaza_id AS plaza_id, 
           pm.phase_id AS phase_id, 
           pm.total_units AS total_units, 
           pm.current_units AS current_units, 
           pm.phase_name AS phase_name, 
           pm.tenant_name AS tenant_name, 
           pm.tenant_email AS tenant_email, 
           pm.tenant_phone AS tenant_phone, 
           IFNULL(SUM(pr.cr), 0) AS total_payment, 
           IFNULL(SUM(pr.dr), 0) AS total_expense, 
           (SELECT IFNULL(SUM(pr2.cr), 0)  - IFNULL(SUM(pr2.dr), 0)
            FROM power_transactions pr2 
            WHERE pr2.tenant_id = pm.tenant_id AND pr2.shop_id = pm.id) AS balance,
           IF(SUM(pr.dr) - SUM(pr.cr) > 0 
              AND MAX(pr.created_at) BETWEEN 
                  _start_date
                  AND _end_date, 
              'yes', 'no') AS has_invoice,
           pm.created_at AS created_at, 
           pm.updated_at AS updated_at
    FROM power_meters pm
    LEFT JOIN power_transactions pr ON pm.tenant_id = pr.tenant_id AND pm.id = pr.shop_id
    WHERE pm.tenant_id IS NOT NULL AND pr.transaction_date BETWEEN _start_date AND _end_date
    GROUP BY pm.id, 
             pm.name, 
             pm.id, 
             pm.shop_no, 
             pm.status, 
             pm.tenant_id, 
             pm.plaza_id, 
             pm.phase_id, 
             pm.total_units, 
             pm.current_units, 
             pm.phase_name, 
             pm.tenant_name, 
             pm.tenant_email, 
             balance
    HAVING SUM(pr.dr) > SUM(pr.cr);   
    ELSEIF query_type='select-meters' THEN
         SELECT pm.id AS id, 
           pm.name AS name, 
           pm.id AS shop_id, 
           pm.shop_no AS shop_no, 
           pm.status AS status, 
           pm.tenant_id AS tenant_id, 
           pm.plaza_id AS plaza_id, 
           pm.phase_id AS phase_id, 
           pm.total_units AS total_units, 
           pm.current_units AS current_units, 
           pr.unit_cost AS unit_cost, 
           pm.phase_name AS phase_name, 
           pm.tenant_name AS tenant_name, 
           pm.tenant_email AS tenant_email, 
           IFNULL(SUM(pr.cr), 0) AS total_payment, 
           IFNULL(SUM(pr.dr), 0) AS total_expense, 
           (SELECT IFNULL(SUM(pr2.dr), 0)-  IFNULL(SUM(pr2.cr), 0)  
            FROM power_transactions pr2 
            WHERE pr2.tenant_id = pm.tenant_id AND pr2.shop_id = pm.id) AS balance,
           IF((SELECT COUNT(*) 
               FROM power_transactions 
               WHERE shop_id = pm.id 
               AND tenant_id = pm.tenant_id 
               AND transaction_type = 'INVOICE' 
               AND cr > 0 
               AND transaction_date BETWEEN _start_date AND _end_date) > 0, 
              'yes', 'no') AS has_invoice,
           pm.created_at AS created_at, 
           pm.updated_at AS updated_at
    FROM power_meters pm
    LEFT JOIN power_transactions pr ON pm.tenant_id = pr.tenant_id AND pm.id = pr.shop_id
    WHERE pm.tenant_id IS NOT NULL
    GROUP BY pm.id,  
             pm.name, 
             pm.id, 
             pm.shop_no, 
             pm.status, 
             pm.tenant_id, 
             pm.plaza_id, 
             pm.phase_id, 
             pm.total_units, 
             pm.current_units, 
             pr.unit_cost,
             pm.phase_name, 
             pm.tenant_name, 
             pm.tenant_email, 
             balance;
  END IF;
END*/;;
